mapper.select:
SELECT *
FROM TB_MINA_TEAM_L
WHERE 1=1

        AND teamId = #{teamId}


        AND teamName LIKE CONCAT('%', #{teamName}, '%')


        AND teamLeaderId = #{teamLeaderId}


        AND teamDesc LIKE CONCAT('%', #{teamDesc}, '%')


        AND createdUserId = #{createdUserId}


        AND updatedUserId = #{updatedUserId}


        AND createDttm = #{createDttm}


        AND updatedDttm = #{updatedDttm}
